/* algorithm to find if there exists a subset of the array that adds up to the target
   input: array of integers, target
   output: boolean (true if there is a subset that adds up to target, false otherwise) */

function findsubset(array, target) {
  let currsum = 0; // holds the current sum of the subset

  for (let i = 0; i < array.length; i++) {
    currsum += array[i]; // try adding each element to the subset

    if (currsum === target) { // if the current sum equals the target, return true
        return true;
    }

    if (currsum > target) { // if the current sum exceeds the target, remove last element from the subset and try again
        currsum -= array[i];
    }
  }
  return false; // if none of the subsets add up to the target, return false
}
